import React from 'react'; // next에선 원래 안써도 됨
import { Input, Form } from 'antd';

const dummy = {
    isLoggedIn: true,
};

const Home = () => {
    // hooks 문법
    return (
        <>
            <div>
                {dummy.isLoggedIn && (
                    <Form encType="multipart/form-data">
                        <Input.TextArea maxLength={140} placeholder="어떤 신기한 일이 잇었나요" />
                    </Form>
                )}
            </div>
        </>
    );
};

export default Home;
