import React from 'react'; // next에선 원래 안써도 됨
import { Input, Form, Button, Card, Avatar, Popover } from 'antd';
import { RetweetOutlined, HeartTwoTone, HeartOutlined, MessageOutlined, EllipsisOutlined } from '@ant-design/icons';

const dummy = {
    isLoggedIn: true,
    imagePaths: [],
    mainPosts: [
        {
            User: {
                id: 1,
                nickname: '제로초',
            },
            content: '첫 번째 게시글',
            img: '',
        },
    ],
};

const Home = () => {
    // hooks 문법
    return (
        <>
            <div>
                {dummy.isLoggedIn && <PostForm />}
                {dummy.mainPosts.map((c) => {
                    return (
                        <Card
                            key={+c.createdAt}
                            cover={c.img && <img alt="example" src={c.img} />}
                            actions={[
                                <RetweetOutlined />,
                                <HeartTwoTone />,
                                <MessageOutlined />,
                                <Popover
                                    key="ellipsis"
                                    content={
                                        <Button.Group>
                                            <Button>신고</Button>
                                            <Button>수정</Button>
                                            <Button danger>삭제</Button>
                                        </Button.Group>
                                    }
                                >
                                    <EllipsisOutlined />
                                </Popover>,
                            ]}
                            extra={<Button>팔로우</Button>}
                        >
                            <Card.Meta
                                avatar={<Avatar>{c.User.nickname[0]}</Avatar>}
                                title={c.User.nickname}
                                description={c.content}
                            ></Card.Meta>
                        </Card>
                    );
                })}
            </div>
        </>
    );
};

export default Home;
