import { all, fork, takeLatest, call, put, take } from 'redux-saga/effects';
import { LOG_IN, LOG_IN_SUCCESS, LOG_IN_FAILURE } from '../reducers/user';

const HELLO_SATA = 'HELLO_SAGA';
function loginAPI() {
    // 서버에 요청을 보내는 동작
}

function* login() {
    // 제너레이터는 async await보다 할 수 있는것이 많다.
    try {
        // 로그인 함수가 동작하면 서버에 먼저 요청을 보내고
        yield call(loginAPI);
        yield put({
            // 성공하면 이 부분 동작한다.
            type: LOG_IN_SUCCESS,
        });
    } catch (e) {
        // loginAPI 실패
        console.error(e);
        yield put({
            type: LOG_IN_FAILURE,
        });
    }
}

function* watchLogin() {
    yield takeLatest(LOG_IN, login); // 로그인 액선이 들어오면 login함수를 실행
}

function* helloSaga() {
    yield take(HELLO_SATA); // take가 HELLO_SAGA를 기다리겠다는 거임. next가 오면 실행하겠다는 거임.
    // take함수 안에 next가 있음. 언제 재게가 되냐면 HELLO_SAGA라는 action이 올 때까지
    console.log('hello saga');
}

export default function* userSaga() {
    yield all([fork(watchLogin), helloSaga()]); // 시작점
}
