import React, { useEffect } from 'react'; // next에선 원래 안써도 됨
import { useDispatch } from 'react-redux';
import PostCard from '../components/PostCard';
import PostForm from '../components/PostForm';
import { LOG_IN, LOG_OUT } from '../reducers/user';

const dummy = {
    isLoggedIn: true,
    imagePaths: [],
    mainPosts: [
        {
            User: {
                id: 1,
                nickname: '제로초',
            },
            content: '첫 번째 게시글',
            img: '',
        },
    ],
};

const Home = () => {
    // hooks
    const dispatch = useDispatch();
    useEffect(() => {
        //로그인 로그아웃 그냥 테스트용
        dispatch({
            type: LOG_IN,
            data: {
                nickname: '제로초',
            },
        });
        dispatch({
            type: LOG_OUT,
            data: {
                nickname: '제로초',
            },
        });
        dispatch({
            type: LOG_IN,
            data: {
                nickname: '제로초',
            },
        });
    }, []);

    return (
        <>
            <div>
                {dummy.isLoggedIn && <PostForm />}
                {dummy.mainPosts.map((c) => {
                    return <PostCard key={c} post={c} />;
                })}
            </div>
        </>
    );
};

export default Home;
