import React from 'react';
import AppLayout from '../components/AppLayout';
import Head from 'next/head';
import { Button, Form, Input, Checkbox } from 'antd';

const Signup = () => {
    // 훅스 사용 실무
    const { id, setId } = useState('');
    const { nick, setNick } = useState('');
    const [password, setPassword] = useState('');

    const onSubmit = () => {};
    const onChangedId = () => {};
    const onChangedNick = () => {};
    const onChangedPass = () => {};
    const onChangedPassChk = () => {};
    const onChangeTerm = () => {};
    return (
        <>
            <Head>
                <title>Node bird</title>
                <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/antd/3.16.2/antd.css" />
                <script src="https://cdnjs.cloudflare.com/ajax/libs/antd/3.16.2/antd.js" />
            </Head>
            <AppLayout>
                <Form onsubmit={onSubmit} style={{ padding: 10 }}>
                    <div>
                        <label htmlFor="user-id">아이디</label>
                        <br />
                        <Input name="user-id" required onChange={onChangedId} />
                    </div>
                    <div>
                        <label htmlFor="user-nick">닉네임</label>
                        <br />
                        <Input name="user-nick" required onChange={onChangedNick} />
                    </div>
                    <div>
                        <label htmlFor="user-pass">비밀번호</label>
                        <br />
                        <Input name="user-pass" type="password" required onChange={onChangedPass} />
                    </div>
                    <div>
                        <label htmlFor="user-pass-chk">비밀번호 확인</label>
                        <br />
                        <Input name="user-pass-chk" type="password" required onChange={onChangedPassChk} />
                    </div>
                    <div>
                        <Checkbox name="user-term" onChange={onChangeTerm}>
                            말을 잘 듣자 동의합니다.
                        </Checkbox>
                    </div>
                    <div>
                        <Button type="primary" htmlType="submit">
                            가입하기
                        </Button>
                    </div>
                </Form>
                <div>회원가입</div>
            </AppLayout>
        </>
    ); //파란색 버튼은 type = primary  html 쓰고 싶으면 htmlType="submit" 써야함 >> button type = "submit"
};

export default Signup;
