import React from 'react'; // next에선 원래 안써도 됨
import { Input, Form, Button, Card, Avatar } from 'antd';
import { RetweetOutlined, HeartTwoTone, HeartOutlined, MessageOutlined, EllipsisOutlined } from '@ant-design/icons';
const dummy = {
    isLoggedIn: true,
    imagePaths: [],
    mainPosts: [
        {
            User: {
                id: 1,
                nickname: '제로초',
                content: '첫 번째 게시글',
            },
        },
    ],
};

const Home = () => {
    // hooks 문법
    return (
        <>
            <div>
                {dummy.isLoggedIn && ( // 이미지 업로드 해야하기 때문에 encType은 multipart임
                    // style넣을 때는 객체 형식으로 {{ 넣어야함 }}
                    <Form encType="multipart/form-data">
                        <Input.TextArea maxLength={140} placeholder="어떤 신기한 일이 잇었나요" />
                        <div>
                            <Button>이미지 업로드</Button>
                            <Button type="primary" style={{ float: 'right' }} htmlType="submit">
                                짹짹쨱
                            </Button>
                        </div>
                        <div>
                            {dummy.imagePaths.map((v, i) => {
                                // 반복문
                                return (
                                    <div key={v} style={{ display: 'inline-block' }}>
                                        <img src={'http://localhost:3000/' + v} style={{ width: '200px' }} alt={v} />
                                        <div>
                                            <Button>제거</Button>
                                        </div>
                                    </div>
                                );
                            })}
                        </div>
                    </Form>
                )}
                {dummy.mainPosts.map((c) => {
                    return (
                        <Card
                            key={+c.createdAt}
                            cover={c.img && <img alt="example" src={c.img} />}
                            actions={[
                                <Icon type="retweet" key="retwwet" />,
                                <Icon type="heart" key="heart" />,
                                <Icon type="message" key="message" />,
                                <Icon type="ellipsis" key="ellipsis" />,
                            ]}
                            extra={<Button>팔로우</Button>}
                        >
                            <Card.Meta
                                avatar={<Avatar>{c.User.nickname[0]}</Avatar>}
                                title={c.User.nickname}
                                description={c.content}
                            ></Card.Meta>
                        </Card>
                    );
                })}
            </div>
        </>
    );
};

export default Home;
